import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Route, Switch } from "react-router-dom";
import SignupFormPage from "./components/SignupFormPage";
import { fetch } from "./store/csrf";
// import LoginFormPage from "./components/LoginFormPage";
import * as sessionActions from "./store/session";
import Navigation from "./components/Navigation";
import Header from "./components/Header";
import Area from "./components/Area";
import Note from "./components/Note";
import Count from "./components/Count";
import Footer from "./components/Footer";

function App() {
  const dispatch = useDispatch();
  const sessionUser = useSelector((state) => state.session.user);
  const [isLoaded, setIsLoaded] = useState(false);
  const [notes, setNotes] = useState([]);
  const [tempnote, setTempNote] = useState([]);

  // const holdTempNote = () => {
  //   setTempNote((prevNote) => {
  //     return [...prevNote.filter((note, index))];
  //   });
  // };

  const addNote = async (newNote) => {
    const res = await fetch("/api/note/", {
      method: "post",
      body: JSON.stringify({
        title: newNote.title,
        textbody: newNote.textbody,
        user_id: sessionUser.id,
      }),
    });
    console.log(res);
    setNotes((prevValue) => {
      return [...prevValue, newNote];
    });
  };

  const deleteNote = async (id) => {
    const res = await fetch("/api/note/", );
    setNotes((prevValue) => {
      return [...prevValue.filter((note, index) => index !== id)];
    });
  };

  useEffect(() => {
    dispatch(sessionActions.restoreUser()).then(() => setIsLoaded(true));
  }, [dispatch]);

  return (
    <>
      <div className="App">
        <Header />
        <Count
          count={
            notes.length === 0
              ? "Inspire yourself to something creative"
              : `Continue Writing notes. Is ${notes} enough? keep pushing!`
          }
        />
        <Area onAdd={addNote} />
        {notes.map((note, index) => (
          <Note
            key={index}
            id={index}
            title={note.title}
            textbody={note.textbody}
            onDelete={deleteNote}
          />
        ))}
        {/* <ReactLogo className="svg" size={30} /> */}
        <Footer />
      </div>
      <Navigation isLoaded={isLoaded} />
      {isLoaded && (
        <Switch>
          {/* <Route path="/login" >
            <LoginFormPage />
          </Route> */}
          <Route path="/signup">
            <SignupFormPage />
          </Route>
        </Switch>
      )}
    </>
  );
}

export default App;
