import React, { useState } from "react";

import { MdDelete } from "react-icons/md";
import EditIcon from "@material-ui/icons/Edit";

const Note = ({ title, textbody, onDelete, id, onEdit }) => {
  const [newTitle, setNewTitle] = useState(title);
  const [newTextBody, setnewTextBody] = useState(textbody);
  const [editing, setEditing] = useState(false);

  const noteUpdater = (
    <form
      onSubmit={() => {
        setEditing
        return (onEdit = { newTitle, newTextBody, id });
      }}
    >
      <input
        type="text"
        value={newTitle}
        onChange={(e) => {
          return setNewTitle(e.target.value);
        }}
      ></input>
      <input
        type="text"
        value={newTextBody}
        onChange={(e) => {
          return setnewTextBody(e.target.value);
        }}
      ></input>
      <button name="editButton" type="submit">
        Submit
      </button>
    </form>
  );

  return (
    <div className="note">
      {editing ? (
        noteUpdater
      ) : (
        <>
          <h3>{newTitle}</h3>
          <p>{newTextBody}</p>
          <button onClick={() => onDelete(id)}>
            <MdDelete className="mcdelete" size={25} />
          </button>
          <button onClick={() => setEditing(true)}>
            <EditIcon className="edit-icon" size={25} />
          </button>
        </>
      )}
    </div>
  );
};

export default Note;
