import React, { useState } from "react";

import { MdDelete } from "react-icons/md";
import EditIcon from "@material-ui/icons/Edit";

const Note = ({ title, textbody, onDelete, id, onEdit }) => {
  const [newTitle, setNewTitle] = useState("");
  const [newTextBody, setnewTextBody] = useState("");
  const [editing, setEditing] = useState(false);

  const noteUpdater = (
    <form
      onSubmit={() => {
        return (onEdit = { newTitle, newTextBody, id });
      }}
    >
      <input
        type="text"
        value={title}
        onChange={(e) => {
          return setNewTitle(e.target.value);
        }}
      ></input>
      <input
        type="text"
        value={textbody}
        onChange={(e) => {
          return setnewTextBody(e.target.value);
        }}
      ></input>
      <button name="editButton" type="submit">
        Submit
      </button>
    </form>
  );

  return (
    <div className="note">
      {editing ? noteUpdater : {} }

    </div>
  );
};

export default Note;
